import React, { useEffect, useState, useRef } from 'react';
import { useAuth } from '../../context/AuthContext';
import api from '../../api/api';
import PageTransition from '../../components/PageTransition';

const ResidentDashboard = () => {
    const { user } = useAuth();
    const [upcomingVisitors, setUpcomingVisitors] = useState([]);
    const [pendingRequests, setPendingRequests] = useState([]); 
    const [unpaidInvoices, setUnpaidInvoices] = useState([]);
    const [loading, setLoading] = useState(true);
    const prevRequestCountRef = useRef(0);

    const fetchData = async () => {
        try {
            const visitorRes = await api.get('/visitors/resident');
            const upcoming = visitorRes.data.filter(v => v.approvalStatus === 'approved' && !v.checkInTime && v.preApproved).slice(0, 3);
            setUpcomingVisitors(upcoming);

            const requestRes = await api.get('/visitors/resident/pending-requests');
            const currentRequests = requestRes.data;
            setPendingRequests(currentRequests);

            if (currentRequests.length > prevRequestCountRef.current) { /* notify logic */ }
            prevRequestCountRef.current = currentRequests.length;

            const invoiceRes = await api.get('/invoices/my');
            const unpaid = invoiceRes.data.filter(i => i.status === 'pending' || i.status === 'overdue');
            setUnpaidInvoices(unpaid);
        } catch (err) { } finally { setLoading(false); }
    };

    useEffect(() => {
        fetchData();
        const interval = setInterval(fetchData, 5000); 
        return () => clearInterval(interval);
    }, []);

    const handleResponse = async (id, status) => {
        try {
            await api.put(`/visitors/resident/respond-request/${id}`, { status });
            setPendingRequests(prev => prev.filter(req => req._id !== id));
            prevRequestCountRef.current = Math.max(0, prevRequestCountRef.current - 1);
            fetchData(); 
        } catch (err) { alert('Failed.'); }
    };

    if (loading) return <div className="text-center mt-5">Loading...</div>;

    return (
        <PageTransition>
            <div className="container mt-4">
                <h2>Welcome, {user.name}! <small className="text-muted fs-6">(Villa {user.unitNumber})</small></h2>
                <hr className="my-4"/>

                {pendingRequests.length > 0 && (
                    <div className="alert alert-warning shadow-sm mb-4">
                        <h5 className="alert-heading fw-bold">ðŸ”” Sudden Visitor at Gate</h5>
                        <div className="list-group mt-2">
                            {pendingRequests.map(req => (
                                <div key={req._id} className="list-group-item d-flex justify-content-between align-items-center">
                                    <div>
                                        <strong>{req.name}</strong> <span className="badge bg-secondary">{req.visitorType}</span>
                                        <br/><small className="text-muted">{new Date(req.createdAt).toLocaleTimeString()}</small>
                                    </div>
                                    <div>
                                        <button className="btn btn-sm btn-success me-2" onClick={() => handleResponse(req._id, 'approved')}>Allow</button>
                                        <button className="btn btn-sm btn-danger" onClick={() => handleResponse(req._id, 'denied')}>Deny</button>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                )}

                <div className="row g-4">
                    <div className="col-md-6">
                        <div className="card h-100 accent-visitor">
                            <div className="card-header accent-header d-flex justify-content-between align-items-center">
                                <span>Upcoming Visitors</span>
                                <a href="/resident/visitors" className="btn btn-sm btn-primary rounded-pill">+ Add</a>
                            </div>
                            <div className="card-body">
                                {upcomingVisitors.length === 0 ? (
                                    <p className="text-muted text-center py-4">No visitors scheduled.</p>
                                ) : (
                                    <ul className="list-group list-group-flush">
                                        {upcomingVisitors.map(v => (
                                            <li key={v._id} className="list-group-item d-flex justify-content-between px-0">
                                                <strong>{v.name}</strong>
                                                <span className="badge bg-primary">{new Date(v.expectedTime).toLocaleTimeString([], {hour:'2-digit', minute:'2-digit'})}</span>
                                            </li>
                                        ))}
                                    </ul>
                                )}
                            </div>
                        </div>
                    </div>

                    <div className="col-md-6">
                        <div className="card h-100 accent-invoice">
                            <div className="card-header accent-header text-danger">Unpaid Invoices</div>
                            <div className="card-body">
                                {unpaidInvoices.length === 0 ? (
                                    <div className="text-center py-4 text-success">
                                        <h4 className="mb-0">ðŸŽ‰</h4>
                                        <p>All cleared!</p>
                                    </div>
                                ) : (
                                    <ul className="list-group list-group-flush">
                                        {unpaidInvoices.map(i => (
                                            <li key={i._id} className="list-group-item d-flex justify-content-between align-items-center px-0">
                                                <div>
                                                    <span className="fw-bold">${i.amount}</span>
                                                    <br/><small className="text-danger">Due: {new Date(i.dueDate).toLocaleDateString()}</small>
                                                </div>
                                                <a href="/resident/payments" className="btn btn-sm btn-outline-danger rounded-pill">Pay</a>
                                            </li>
                                        ))}
                                    </ul>
                                )}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </PageTransition>
    );
};

export default ResidentDashboard;
